// src/components/TaskList.js
import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { deleteTask, editTask, toggleTask } from '../redux/actions';

const TaskList = () => {
  const tasks = useSelector((state) => state.tasks);
  const dispatch = useDispatch();

  return (
    <ul>
      {tasks.map((task, index) => (
        <li key={index} style={{ textDecoration: task.completed ? 'line-through' : 'none' }}>
          {task.task}
          <button onClick={() => dispatch(toggleTask(index))}>
            {task.completed ? 'Undo' : 'Complete'}
          </button>
          <button onClick={() => dispatch(deleteTask(index))}>Delete</button>
          <button onClick={() => dispatch(editTask(index, prompt('Edit task:', task.task)))}>Edit</button>
        </li>
      ))}
    </ul>
  );
};

export default TaskList;
